function syncTrackers(){
	var r = new sn_ws.RESTMessageV2('x_snc_k18_mars.syncTrackers', 'sync');
	r.setStringParameterNoEscape('pod_url', gs.getProperty('glide.servlet.uri'));
	var body = '[{"description":"Description 1","module_name":"Module 11111"},{"description":"Description 2","module_name":"Module 2"},{"description":"Description 3","module_name":"Module 3"}]';
	r.setRequestBody(body);
	var response = r.execute();
	var httpStatus = response.getStatusCode();
	return response.getBody();
}
