sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {


	return x_snc_k18_mars.updateCrew(request.body.data.sys_id,request.body.data.complete);

	function updateCrew(astronaut,complete){
		//take sys_id from astronaut table then creates crew record
		//reset crew if found

		var crew = new GlideRecord('x_snc_k18_mars_m2m_astronauts_missions');
		crew.get(astronaut);
		var gdt = new GlideDateTime();
		crew.last_check_in=gdt.getDisplayValue();
		crew.complete=complete;
		crew.status='in_progress';
		if (complete==100){
			crew.completion_date=gdt.getDisplayValue();
			crew.status='complete';
			crew.completion_time=GlideDateTime.subtract(new GlideDateTime(crew.mission.launch_time.getDisplayValue()), new GlideDateTime(gdt.getDisplayValue()));
		}
		crew.update();
		var returnObj = {};
		returnObj.crew={};
		returnObj.crew.sys_id=crew.sys_id.getDisplayValue();
		returnObj.crew.last_check_in=crew.last_check_in.getDisplayValue();
		returnObj.crew.status=crew.status.getDisplayValue();
		returnObj.crew.complete=crew.complete.getDisplayValue();
		returnObj.crew.completion_time=crew.completion_time.getDisplayValue();
		returnObj.crew.time_left=x_snc_k18_mars.getTimeLeft(crew.sys_id.getDisplayValue());
	} 


})(request, response);
